import React, { useState } from "react";
import { StyleSheet, Text, View } from "react-native";
import MenuBar from "./components/MenuBar";
import colors from "./helpers/colors";
import Screen from "./components/Screen";

export default function App() {
  const [content, setContent] = useState("Bienvenidos a MercadoVargas");

  const menuHandler = (content) => setContent(`${content}`);

  return (
    <Screen>
      <Image
        // style={styles.tinyLogo}
        source={require("@expo/snack-static/react-native-logo.png")}
      />
      <View style={styles.mainContainer}>
        {/* Here starts different views content */}

        <Text>{content}</Text>

        {/* Here ends different views content */}
      </View>
      <MenuBar menuHandler={menuHandler} />
    </Screen>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.primary,
    alignItems: "center",
    justifyContent: "center",
  },
  mainContainer: {
    backgroundColor: "dodgerblue",
    flex: 1,
    width: "100%",
    // position: "absolute",
    // top: 0,
  },
});
