import React from "react";
import { Image, Text, View, StyleSheet, TextInput, Button } from "react-native";
import colors from "../helpers/colors";

function LoginView(props) {
  return (
    <View style={styles.loginStyle}>
      <View style={styles.greenTop}></View>
      <View style={styles.whiteCircle}>
        <Image
          style={styles.vargasLogo}
          source={require("../assets/mercadoVargasLogo.png")}
        />
      </View>
      <View style={styles.loginInputContainer}>
        <Text style={styles.loginInputLabel}>Email</Text>
        <TextInput style={styles.loginInputFields}></TextInput>
        <Text style={styles.loginInputLabel}>Password</Text>
        <TextInput style={styles.loginInputFields}></TextInput>
        <Button
          // color="#f194ff"
          style={styles.loginButton}
          title="Log In"
          onPress={() => Alert.alert("Button with adjusted color pressed")}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  greenTop: {
    backgroundColor: colors.green100,
    height: 200,
    width: 440,
    transform: [{ translateX: 0 }, { translateY: -100 }, { rotate: "-10deg" }],
  },
  whiteCircle: {
    backgroundColor: colors.white,
    height: 120,
    width: 120,
    borderRadius: 100,
    transform: [{ translateY: -160 }],
    alignItems: "center",
    justifyContent: "center",
  },
  loginStyle: {
    alignItems: "center",
    justifyContent: "center",
  },
  loginInputContainer: {
    transform: [{ translateY: -130 }],
  },
  loginInputLabel: {
    marginLeft: 10,
    marginRight: 10,
    color: colors.green100,
  },
  loginInputFields: {
    backgroundColor: colors.greenlogin,
    borderColor: colors.greenlogin,
    borderWidth: 2,
    borderRadius: 5,
    height: 30,
    marginLeft: 10,
    marginRight: 10,
    width: 250,
  },
  loginButton: {
    backgroundColor: "red",
    color: "red",
    color: "red",
    borderBottomColor: "red",
    // backgroundColor: "#1E6738",
    // borderRadius: 10,
    // borderWidth: 1,
    // borderColor: "#fff",
  },
  vargasLogo: {
    height: 110,
    width: 110,
  },
});

export default LoginView;
