import React from "react";
import { Text, View, StyleSheet, TouchableHighlight } from "react-native";
// import { Ionicons, Entypo } from "@expo/vector-icons";
// import Fonticon from "../helpers/fontIcons";
import iconHelper from "../helpers/iconHelper";

import colors from "../helpers/colors";

function MenuBarItem(props) {
  return (
    <TouchableHighlight
      style={styles.menuBarItem}
      onPress={() => {
        // console.log(`Pressing ${props.title}`);
        props.menuHandler(props.title);
      }}
    >
      <View style={styles.menuBarItemContainer}>
        {/* <Fonticon type="Entypo" name="home" size={10} color="green" /> */}
        {/* <Entypo name="home" size={32} color="green" /> */}
        {iconHelper(...props.icon)}
        {/* <{props.iconFamily} name="home" size={32} color="green" /> */}
        <Text>
          {props.title} {props.iconFamily}
        </Text>
      </View>
    </TouchableHighlight>
  );
}

const styles = StyleSheet.create({
  menuBarItem: {
    width: "25%",
  },
  menuBarItemContainer: {
    backgroundColor: colors.secondary,
    margin: 10,
    height: 40,
    alignItems: "center",
    justifyContent: "center",
  },
});

export default MenuBarItem;
