import React from "react";
import { Text, View, StyleSheet, TouchableHighlight } from "react-native";
import { Ionicons, Entypo } from "@expo/vector-icons";

import colors from "../helpers/colors";

function MenuBarItem(props) {
  return (
    <TouchableHighlight
      style={styles.menuBarItem}
      onPress={() => {
        // console.log(`Pressing ${props.title}`);
        props.menuHandler(props.title);
      }}
    >
      <View style={styles.menuBarItemContainer}>
        <Ionicons name={props.title} size={32} color="green" />
        <Text>{props.title}</Text>
      </View>
    </TouchableHighlight>
  );
}

const styles = StyleSheet.create({
  menuBarItem: {
    width: "25%",
  },
  menuBarItemContainer: {
    backgroundColor: colors.secondary,
    margin: 10,
    height: 40,
    alignItems: "center",
    justifyContent: "center",
  },
});

export default MenuBarItem;
